css:
#whatsapp-balloon {
  position: fixed;
  bottom: 48px;
  right: 48px;
  z-index: 9999;
  transition: transform 0.3s ease-in-out;
}

#whatsapp-balloon:hover {
  transform: scale(1.1);
}
.lightwidget-widget {
  max-width: 500px;
  margin: 20 auto;
}

.lightwidget-widget iframe {
  width: 100%;
  height: 500px;
}
.lightwidget-widget {
  position: footer;
  bottom: 0;
  left: 0;
  width: 100%;
  max-width: 500px;
  margin: 0 auto;
}
