css:
body {
  margin: 0;
}

#whatsapp-balloon {
  position: fixed;
  bottom: 48px;
  right: 48px;
  z-index: 9999;
  transform: scale(0);
  opacity: 0;
  transition: transform 0.5s ease-in-out, opacity 0.5s ease-in-out;
}

#whatsapp-balloon:hover {
  transform: scale(1.1);
}

@keyframes fadeIn {
  from {
    transform: scale(0);
    opacity: 0;
  }
  to {
    transform: scale(1);
    opacity: 1;
  }
}

#whatsapp-balloon.animation {
  animation: fadeIn 1s ease-in-out;
  animation-fill-mode: forwards; /* Garante que o botão permaneça no estado final da animação */
}
}
.image-container {
  max-width: 700px;
  margin: 0 auto;
}

.row {
  display: flex;
  justify-content: space-between;
  margin-bottom: 5px;
}

.image-box {
  flex: 1;
  margin: 5px;
  border: 1px solid #ccc;
  overflow: hidden;
}

.image-box img {
  width: 100%;
  height: auto;
}

.lightwidget-widget {
  max-width: 500px;
  margin: 20 auto;
}

.lightwidget-widget iframe {
  width: 100%;
  height: 500px;
}
.lightwidget-widget {
  position: footer;
  bottom: 0;
  left: 0;
  width: 100%;
  max-width: 500px;
  margin: 0 auto;
}
